                {
                    bootStrapCSS.map(cssPath => <link key={cssPath} rel="stylesheet" href={cssPath}></link>)
                }
            </head>
            <body>
                <div id="root-store">
                    <Dashboard locationHref={locationHref} />
                </div>
            </body>
        </html>
    )
}

StoreSSR.propTypes = {
    bootStrapCSS: PropTypes.arrayOf(PropTypes.string).isRequired
}

export default StoreSSR;

