app.get("/stats/:email", function (req, res) {
  var client = new Client(dbConfig);
  var email = req.params.email;
  var supportMobile = '';
  email = email.replace('owner@', '@');
  var franchiseId = '';
  var storeId = '';
  client.connect(function (err) {
    if (err) {
      console.error('error connecting', err.stack);
      res.send('{"status":"connect-error"}');
      client.end();
    } else {
      client.query("Select id from am_franchise where owner_email IN ('" + email + "') ", [], function (err, response) {
        if (err) {
          console.log(err);
          res.send('{"status":"response-error"}');
          client.end();
        } else {
          //res.send(response.rows);
          if (response.rows.length == 0) {
            res.send('{"status":"no-results"}');
            client.end();
          } else {
            franchiseId = response.rows[0]['id'];
            client.query("Select id, support_mobile from am_store where franchise_id IN ('" + franchiseId + "') ", [], function (errInner, responseInner) {
              if (errInner) {
                res.send('{"status":"inner-connect-error"}');
                client.end();
              } else {
                storeId = responseInner.rows[0]['id'];
                supportMobile = responseInner.rows[0]['support_mobile'];
                res.send('{"franchiseId":' + franchiseId + ',"storeId":' + storeId + ',"supportMobile":' + supportMobile + '}');
              }
            });
          }
          //res.send('{"franchiseId":'+franchiseId+',"storeId":'+storeOrders+'}');
        }
      });
    }
  });
});
