import React from "react";
import ProductCard from "./ProductCard";
import "./ProductGrid.css";

const FavouriteGrid = ({ gridLoading, products = [] }) => {
  return (
    <div className={`product-grid ${gridLoading ? 'grid-loading' : ''}`}>
      {products.map((product) => (
          <div className="product-card" onClick={(e)=>{window.location.href=product.product_link}}>
            <img
              src={product.thumbnail}
              alt={product.title}
              style={{ height: '300px' }}
            />
            <div className="card-content" style={{background: `linear-gradient(rgba(0, 0, 0, 0), rgb(0 0 0 / 0%)), url(${product.imageUrls['800x800']})`, filter: 'blur(36px)',backgroundSize: '1px'}}>
              <h3 style={{fontWeight: 400}}>{product.title}</h3>
              <p className="price">â‚¹{product.price}</p>
            </div>
            <div className="card-content-desc">
              <p className="price-range">{product.price}</p>
              <span className="cta-wishlist price-range cta-shop" style={{ background: '#000'}} onClick={()=>{window.location.href=product.product_link}}>Shop Now</span>
            </div>
        </div>
      ))}
    </div>
  );
};

export {FavouriteGrid};
