import { useState, createRef } from "react";
import axios from 'axios';
import "./ViewProducts.css";

const ProductList = ({products}) => {

    return (
      <div className="product-list">
        {products.map((p, index) => {
          const originalPrice = Math.round(p.price * 1.2);

          return (
            <div key={index} className="card">
              <img src={p.image_url} alt={p.title} />
              <div className="card-content">
                <div className="highlights">{p.highlights}</div>
                <div className="description">{p.description}</div>
                <div className="price">
                  <div className="price-current">₹{p.price}</div>
                  <div className="price-original">₹{originalPrice}</div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    );
  };

  const NestedTabs = (categories) => {
    const [path, setPath] = useState([]);

    const getValueAtPath = (obj, path) =>
      path.reduce((acc, key) => (acc && acc[key] ? acc[key] : null), obj);

    const currentData = getValueAtPath(categories, path);

    const isLeaf = (val) =>
      Array.isArray(val) || typeof val !== 'object' || val === null;

    const handleClick = (key) => {
      setPath((prevPath) => [...prevPath, key]);
    };

    const handleBack = () => {
      setPath((prev) => prev.slice(0, -1));
    };

    return (
      <div style={{ padding: '1rem' }}>
        {path.length > 0 && (
          <button onClick={handleBack} style={{ marginBottom: '0.5rem' }}>
            ← Back
          </button>
        )}

        <div className="tabs-container">
          <div className="tabs">
            {isLeaf(currentData)
              ? currentData.map((item, i) => (
                  <div className="tab" key={i}>
                    {item}
                  </div>
                ))
              : Object.keys(currentData).map((key) => (
                  <div className="tab" key={key} onClick={() => handleClick(key)}>
                    {key}
                  </div>
                ))}
          </div>
        </div>
      </div>
    );
  };



const ViewProducts = ({url}) => {
    const [message, setMessage] = useState("");
    const [products, setProducts] = useState([]);
    const categories = {
        "mens": {"shirts" : { "formals": {"xl": ["black", "white"]}, "casuals": { "T-shirt": {"m": ["black", "white"], "l": ["black", "white"]} }}}
    };


      const storeId = JSON.parse(window.sessionStorage.getItem('user-profile')).storeId; 

      if (products.length == 0) {
        axios.get(`/products/${storeId}`)
        .then(function (response) {
          if(response.data != 'auth error') {
              console.log('--product res--', response.data);
              setProducts(response.data);
          }
        }.bind(this));
      }

    return (
        <div className="main">
           <div class="container">
                <h1 style={{marginLeft: '16px'}}>View Products</h1>
                <div class="tabs-container">
                    <div class="tabs" id="main-tabs">
                    <div class="tab active">All</div>
                    <div class="tab">Season's Special</div>
                    <div class="tab">New Arrivals</div>
                    </div>
                    <div class="sub-tabs" id="sub-tabs">
                    <NestedTabs categories={categories} />
                    </div>
                </div>
                <ProductList products={products} />
            </div>
        </div>
    );
}

export default ViewProducts;
