import "./Header.css";

const onLogoutClick = () => {
    console.log('--clicked log out--');
    alert('Log out');
}

const getLogoSrc = () => {
    let storeFolder = '';
    if (location.href.indexOf('www.amuzely.com') == -1 && location.href.indexOf('.amuzely.com') != -1) {
        const storeFolder = location.href.substring(location.href.indexOf('https://')+'https://'.length,location.href.indexOf('.amuzely.com'));
    }
    else {
        storeFolder = '../../app/swirlyojpnagar/images/logo.png';
    }
    return `../../app/${storeFolder}/images/logo.png`;
}

const Header = (props) => {
    const logoSrc = getLogoSrc();

    return <div
        className="header">{""}
            <img className="store-logo" src={logoSrc} />
            {props.loggedOut &&
                 <span id="logout" className="logout" onClick={onLogoutClick}>Logout</span>}
        </div>
}

export default Header;
